function UpdateRelativeElements(){var n=document.getElementsByClassName("fsa-relative");for(let u=0;u<n.length;u++){var t=n[u],i=t.getBoundingClientRect(),r=invlerp(window.innerHeight,-i.height,i.top);t.style.setProperty("--animation-time",r)}}function UpdateScreenPerspective(){var i=document.getElementsByClassName("scene-screen-perspective");for(let u=0;u<i.length;u++){var n=i[u],t=n.getBoundingClientRect(),r=invlerp(-t.height,window.innerHeight,t.top);n.style.perspectiveOrigin="50% "+(window.innerHeight/t.height*(r*2-1)*-100+50)+"%";n.style.perspective=1024*window.devicePixelRatio+"px"}}const clamp=(n,t=0,i=1)=>Math.min(i,Math.max(t,n)),invlerp=(n,t,i)=>clamp((i-n)/(t-n));window.addEventListener("scroll",()=>{UpdateRelativeElements(),UpdateScreenPerspective()},!1);window.addEventListener("resize",()=>{UpdateRelativeElements(),UpdateScreenPerspective()},!1);UpdateRelativeElements();UpdateScreenPerspective();