function UpdateRelativeElements(){var n=document.getElementsByClassName("fsa-relative");for(let u=0;u<n.length;u++){var t=n[u],i=t.getBoundingClientRect(),r=invlerp(-i.height,screen.height,i.top);t.style.setProperty("--scroll",r)}}const clamp=(n,t=0,i=1)=>Math.min(i,Math.max(t,n)),invlerp=(n,t,i)=>clamp((i-n)/(t-n));window.addEventListener("scroll",()=>{UpdateRelativeElements()},!1);window.addEventListener("resize",()=>{UpdateRelativeElements()},!1);UpdateRelativeElements();